const profileUpdate=$("#update-profile");profileUpdate.on("submit",function(e){e.preventDefault();const t=new FormData(this);$.ajax({type:"POST",url:"/user/update-profile",data:t,enctype:"multipart/form-data",processData:!1,contentType:!1,success:function(e){const{data:{name:t="",avatar:a=""}={},success:o}=e;$("#profile-name").html(t),$("#profile-image").remove(),$("#profile-image-container").html(`<img id="profile-image" height="100px" width="100px" src="${a}" alt="${t}" />`),o&&new Noty({theme:"relax",text:`${o}`,type:"success",layout:"topRight",timeout:1500}).show()}})});